  1import { db, eq, schema } from "@/lib/db";
  2import { githubAppEnv } from "@/lib/env";
  3import { getInstallationRepositories, getRepositoryById } from "@/lib/github";
  4import { TRPCError } from "@trpc/server";
  5import { z } from "zod";
  6import { t, workspaceProcedure } from "../trpc";
  7
  8const state = z.object({
  9  projectId: z.string().min(1),
 10});
 11
 12const fetchGithubContext = async (workspaceId: string, projectId: string) => {
 13  const project = await db.query.projects
 14    .findFirst({
 15      where: (table, { and, eq }) =>
 16        and(eq(table.id, projectId), eq(table.workspaceId, workspaceId)),
 17      columns: {
 18        id: true,
 19      },
 20      with: {
 21        githubRepoConnection: {
 22          columns: {
 23            pk: true,
 24            repositoryId: true,
 25            repositoryFullName: true,
 26          },
 27        },
 28        workspace: {
 29          columns: {
 30            id: true,
 31          },
 32          with: {
 33            githubAppInstallations: {
 34              columns: {
 35                pk: true,
 36                installationId: true,
 37              },
 38            },
 39          },
 40        },
 41      },
 42    })
 43    .catch(() => {
 44      throw new TRPCError({
 45        code: "INTERNAL_SERVER_ERROR",
 46        message: "Failed to load GitHub context",
 47      });
 48    });
 49
 50  if (!project) {
 51    return null;
 52  }
 53
 54  return {
 55    repoConnection: project.githubRepoConnection
 56      ? {
 57          pk: project.githubRepoConnection.pk,
 58          repositoryId: project.githubRepoConnection.repositoryId,
 59          repositoryFullName: project.githubRepoConnection.repositoryFullName,
 60        }
 61      : null,
 62    installations: project.workspace?.githubAppInstallations ?? [],
 63  };
 64};
 65
 66const fetchProjectInstallation = async (
 67  workspaceId: string,
 68  projectId: string,
 69  installationId: number,
 70) => {
 71  const project = await db.query.projects
 72    .findFirst({
 73      where: (table, { and, eq }) =>
 74        and(eq(table.id, projectId), eq(table.workspaceId, workspaceId)),
 75      columns: {
 76        id: true,
 77      },
 78      with: {
 79        workspace: {
 80          columns: {
 81            id: true,
 82          },
 83          with: {
 84            githubAppInstallations: {
 85              where: (table, { eq }) => eq(table.installationId, installationId),
 86              columns: {
 87                pk: true,
 88              },
 89              limit: 1,
 90            },
 91          },
 92        },
 93      },
 94    })
 95    .catch(() => {
 96      throw new TRPCError({
 97        code: "INTERNAL_SERVER_ERROR",
 98        message: "Failed to load GitHub installation",
 99      });
100    });
101
102  if (!project) {
103    return null;
104  }
105
106  return {
107    installationPk: project.workspace?.githubAppInstallations?.[0]?.pk ?? null,
108  };
109};
110
111export const githubRouter = t.router({
112  registerInstallation: workspaceProcedure
113    .input(
114      z.object({
115        state: z.string(),
116        installationId: z.number().int(),
117      }),
118    )
119    .mutation(async ({ ctx, input }) => {
120      if (!githubAppEnv()) {
121        throw new TRPCError({
122          code: "INTERNAL_SERVER_ERROR",
123          message: "GitHub App not configured",
124        });
125      }
126      let parsedState: z.infer<typeof state> | null = null;
127      try {
128        const result = state.safeParse(JSON.parse(input.state));
129        parsedState = result.success ? result.data : null;
130      } catch {
131        parsedState = null;
132      }
133      if (!parsedState) {
134        throw new TRPCError({
135          code: "BAD_REQUEST",
136          message: "Invalid callback state",
137        });
138      }
139
140      const projectId = parsedState.projectId;
141      const projectInstallation = await fetchProjectInstallation(
142        ctx.workspace.id,
143        projectId,
144        input.installationId,
145      ).catch((err) => {
146        console.error(err);
147        throw new TRPCError({
148          code: "INTERNAL_SERVER_ERROR",
149          message: "Failed to load project installation",
150        });
151      });
152
153      if (!projectInstallation) {
154        throw new TRPCError({
155          code: "NOT_FOUND",
156          message: "Project not found",
157        });
158      }
159
160      await db
161        .insert(schema.githubAppInstallations)
162        .values({
163          workspaceId: ctx.workspace.id,
164          installationId: input.installationId,
165          createdAt: Date.now(),
166          updatedAt: null,
167        })
168        .onDuplicateKeyUpdate({
169          set: {
170            updatedAt: Date.now(),
171          },
172        })
173        .catch((err) => {
174          console.error(err);
175          throw new TRPCError({
176            code: "INTERNAL_SERVER_ERROR",
177            message: "Failed to save GitHub installation",
178          });
179        });
180
181      return {
182        workspaceSlug: ctx.workspace.slug,
183        projectId,
184      };
185    }),
186
187  getInstallations: workspaceProcedure
188    .input(
189      z.object({
190        projectId: z.string(),
191      }),
192    )
193    .query(async ({ ctx, input }) => {
194      const githubContext = await fetchGithubContext(ctx.workspace.id, input.projectId);
195      if (!githubContext) {
196        throw new TRPCError({
197          code: "NOT_FOUND",
198          message: "Project not found",
199        });
200      }
201
202      return {
203        installations: githubContext.installations,
204        repoConnection: githubContext.repoConnection,
205      };
206    }),
207
208  listRepositories: workspaceProcedure
209    .input(
210      z.object({
211        projectId: z.string(),
212      }),
213    )
214    .query(async ({ ctx, input }) => {
215      const githubContext = await fetchGithubContext(ctx.workspace.id, input.projectId);
216      if (!githubContext) {
217        throw new TRPCError({
218          code: "NOT_FOUND",
219          message: "Project not found",
220        });
221      }
222
223      if (githubContext.installations.length === 0) {
224        return { repositories: [] };
225      }
226
227      const allRepos: Array<{
228        id: number;
229        name: string;
230        fullName: string;
231        private: boolean;
232        htmlUrl: string;
233        defaultBranch: string;
234        installationId: number;
235      }> = [];
236
237      for (const installation of githubContext.installations) {
238        const repos = await getInstallationRepositories(installation.installationId).catch(
239          (err) => {
240            console.error(err);
241            throw new TRPCError({
242              code: "INTERNAL_SERVER_ERROR",
243              message: "Failed to load GitHub repositories",
244            });
245          },
246        );
247        for (const repo of repos) {
248          allRepos.push({
249            id: repo.id,
250            name: repo.name,
251            fullName: repo.full_name,
252            private: repo.private,
253            htmlUrl: repo.html_url,
254            defaultBranch: repo.default_branch,
255            installationId: installation.installationId,
256          });
257        }
258      }
259
260      allRepos.sort((a, b) => a.fullName.localeCompare(b.fullName));
261
262      return { repositories: allRepos };
263    }),
264
265  selectRepository: workspaceProcedure
266    .input(
267      z.object({
268        projectId: z.string(),
269        repositoryId: z.number().int(),
270        repositoryFullName: z.string(),
271        installationId: z.number().int(),
272      }),
273    )
274    .mutation(async ({ ctx, input }) => {
275      const projectInstallation = await fetchProjectInstallation(
276        ctx.workspace.id,
277        input.projectId,
278        input.installationId,
279      );
280
281      if (!projectInstallation) {
282        throw new TRPCError({
283          code: "NOT_FOUND",
284          message: "Project not found",
285        });
286      }
287
288      if (projectInstallation.installationPk === null) {
289        throw new TRPCError({
290          code: "NOT_FOUND",
291          message: "GitHub installation not found",
292        });
293      }
294
295      const verifiedRepo = await getRepositoryById(input.installationId, input.repositoryId).catch(
296        () => {
297          throw new TRPCError({
298            code: "INTERNAL_SERVER_ERROR",
299            message: "Failed to verify GitHub repository",
300          });
301        },
302      );
303
304      if (!verifiedRepo) {
305        throw new TRPCError({
306          code: "NOT_FOUND",
307          message: "Repository not found in the specified GitHub installation",
308        });
309      }
310
311      await db
312        .insert(schema.githubRepoConnections)
313        .values({
314          projectId: input.projectId,
315          installationId: input.installationId,
316          repositoryId: verifiedRepo.id,
317          repositoryFullName: verifiedRepo.full_name,
318          createdAt: Date.now(),
319          updatedAt: null,
320        })
321        .onDuplicateKeyUpdate({
322          set: {
323            installationId: input.installationId,
324            repositoryId: verifiedRepo.id,
325            repositoryFullName: verifiedRepo.full_name,
326            updatedAt: Date.now(),
327          },
328        })
329        .catch(() => {
330          throw new TRPCError({
331            code: "INTERNAL_SERVER_ERROR",
332            message: "Failed to save GitHub repository connection",
333          });
334        });
335
336      return { success: true };
337    }),
338
339  disconnectRepo: workspaceProcedure
340    .input(
341      z.object({
342        projectId: z.string(),
343      }),
344    )
345    .mutation(async ({ ctx, input }) => {
346      const project = await db.query.projects
347        .findFirst({
348          where: (table, { and, eq }) =>
349            and(eq(table.id, input.projectId), eq(table.workspaceId, ctx.workspace.id)),
350        })
351        .catch(() => {
352          throw new TRPCError({
353            code: "INTERNAL_SERVER_ERROR",
354            message: "Failed to load project",
355          });
356        });
357
358      if (!project) {
359        throw new TRPCError({
360          code: "NOT_FOUND",
361          message: "Project not found",
362        });
363      }
364
365      await db
366        .delete(schema.githubRepoConnections)
367        .where(eq(schema.githubRepoConnections.projectId, input.projectId))
368        .catch(() => {
369          throw new TRPCError({
370            code: "INTERNAL_SERVER_ERROR",
371            message: "Failed to disconnect GitHub repository",
372          });
373        });
374
375      return { success: true };
376    }),
377
378  removeInstallation: workspaceProcedure
379    .input(
380      z.object({
381        installationId: z.number().int(),
382      }),
383    )
384    .mutation(async ({ ctx, input }) => {
385      const installation = await db.query.githubAppInstallations
386        .findFirst({
387          where: (table, { and, eq }) =>
388            and(
389              eq(table.installationId, input.installationId),
390              eq(table.workspaceId, ctx.workspace.id),
391            ),
392        })
393        .catch(() => {
394          throw new TRPCError({
395            code: "INTERNAL_SERVER_ERROR",
396            message: "Failed to load GitHub installation",
397          });
398        });
399
400      if (!installation) {
401        throw new TRPCError({
402          code: "NOT_FOUND",
403          message: "Installation not found",
404        });
405      }
406
407      await db
408        .delete(schema.githubRepoConnections)
409        .where(eq(schema.githubRepoConnections.installationId, input.installationId))
410        .catch(() => {
411          throw new TRPCError({
412            code: "INTERNAL_SERVER_ERROR",
413            message: "Failed to remove GitHub installation",
414          });
415        });
416
417      await db
418        .delete(schema.githubAppInstallations)
419        .where(eq(schema.githubAppInstallations.installationId, input.installationId))
420        .catch(() => {
421          throw new TRPCError({
422            code: "INTERNAL_SERVER_ERROR",
423            message: "Failed to remove GitHub installation",
424          });
425        });
426
427      return { success: true };
428    }),
429});
